import React, {useState} from "react";
import trashIcon from './tr.png'



function App() {
  const [Todo, setTodo]=useState("")
  const [TodoList,setTodoList]= useState([])

  return (
    <div className="app-background">
      <p className="heading-text">React To-Do List</p>
      <div className="task-container">
        <div>
          <input className="text-input" value={Todo} onChange={(event)=>setTodo(event.target.value)}/>
          <button className="add-button">ADD</button>
        </div>
        {TodoList?.length?<ListItem/>:<p>No task added!</p>}

      </div>
      <p className="footer-text">The Todo Box</p>
    </div>
  );
}


function ListItem(props){
  return(
    <div className="list-item row jc-space-between">
      <span>
        List Item
      </span>
      <img className="delete-item" alt="delete-icon"height={23} src={trashIcon}/>
    </div>
  )
}
export default App;
